import React from "react";
//import { Button } from "/components/ui/button";
import { Button,Card} from 'antd';
//import { Card, CardContent } from "./components/ui/card";
import { Phone, Mail, MapPin } from "lucide-react";
import background from './images/background.jpg';


const Home: React.FC = () => {
  return (
<div
      className="min-h-screen bg-no-repeat bg-cover text-gray-800"
      style={{ backgroundImage: `url(${background})` }}
    >
      {/* Header */}
      <header className="bg-yellow-600 text-white p-6 shadow-md">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold">Ekarit Visa Service</h1>
          <nav className="space-x-6">
            <a href="#services" className="hover:underline">Services</a>
            <a href="#about" className="hover:underline">About</a>
            <a href="#contact" className="hover:underline">Contact</a>
          </nav>
        </div>
      </header>

      {/* Hero Section */}
      <section className="bg-gray-100 py-20 text-center">
        <h2 className="text-4xl font-bold mb-4">Professional Services for your Visa Process</h2>
        <p className="text-lg mb-8">We provide expert services tailored to your needs.</p>
        <Button className="text-white bg-yellow-600 hover:bg-blue-700">Get Started</Button>
      </section>

      {/* Services Section */}
      <section id="services" className="py-20 container mx-auto px-4">
        <h3 className="text-3xl font-bold text-center mb-12">Our Services</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {[
            { title: "Consulting", desc: "Expert advice to guide your business decisions." },
            { title: "Development", desc: "Custom software solutions tailored to your operations." },
            { title: "Support", desc: "Reliable and timely support for your systems." },
          ].map((service, i) => (
            <Card key={i} className="shadow-md">
              
                <h4 className="text-xl font-semibold mb-2">{service.title}</h4>
                <p>{service.desc}</p>
        
            </Card>
          ))}
        </div>
      </section>

      {/* About Section */}
      <section id="about" className="bg-blue-50 py-20 px-4">
        <div className="container mx-auto max-w-4xl text-center">
          <h3 className="text-3xl font-bold mb-6">About Us</h3>
          <p className="text-lg">ServicePro has over 10 years of experience delivering top-tier solutions to clients across industries. Our mission is to empower businesses with scalable and effective service offerings.</p>
        </div>
      </section>

      {/* Contact Section */}
      <section id="contact" className="py-20 container mx-auto px-4">
        <h3 className="text-3xl font-bold text-center mb-12">Contact Us</h3>
        <div className="grid md:grid-cols-3 gap-8 text-center">
          <div>
            <Phone className="mx-auto mb-2 text-blue-600" />
            <p>+1 (555) 123-4567</p>
          </div>
          <div>
            <Mail className="mx-auto mb-2 text-blue-600" />
            <p>info@servicepro.com</p>
          </div>
          <div>
            <MapPin className="mx-auto mb-2 text-blue-600" />
            <p>123 Main Street, City, Country</p>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-yellow-600 text-white text-center py-6">
        <p>&copy; {new Date().getFullYear()} ServicePro. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
