import React from "react";
import { Card, Button, Select, Input, DatePicker } from "antd";

const { Option } = Select;

const products = [
  {
    type: "Savings",
    name: "Rainy Day Fund",
    price: "192,000 MMK /Year",
  },
  {
    type: "Protections",
    name: "Travel",
    price: "100 MMK /Day",
  },
  {
    type: "Protections",
    name: "Personal Accident",
    price: "3,500 MMK /Year",
  },
  {
    type: "Protections",
    name: "Micro Health",
    price: "5,500 MMK /Year",
  },
];

export default function Services() {
  return (
    <div className="min-h-screen bg-white font-sans">
      {/* Products Section */}
      <div className="bg-blue-600 py-10 px-6 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
        {products.map((product, idx) => (
          <Card
            key={idx}
            className="rounded-xl text-center text-white bg-blue-700"
            bodyStyle={{ padding: "20px" }}
          >
            <div className="text-sm font-medium bg-blue-800 inline-block px-3 py-1 rounded-full mb-2">
              {product.type}
            </div>
            <div className="text-lg font-semibold mb-1">{product.name}</div>
            <div className="text-xl font-bold mb-4">{product.price}</div>
            <Button type="primary" className="bg-white text-blue-700 w-full">
              Buy
            </Button>
          </Card>
        ))}
      </div>

      {/* Calculator Section */}
      <div className="bg-gray-100 px-6 py-6 flex flex-col sm:flex-row justify-around items-center gap-4">
        <div className="flex flex-col">
          <label className="mb-1 text-sm font-medium">Select Product</label>
          <Select defaultValue="Universal Life" className="min-w-[200px]">
            <Option value="Universal Life">Universal Life</Option>
          </Select>
        </div>

        <div className="flex flex-col">
          <label className="mb-1 text-sm font-medium">Base Premium</label>
          <Input defaultValue="600000" className="min-w-[150px]" />
        </div>

        <div className="flex flex-col">
          <label className="mb-1 text-sm font-medium">Date Of Birth</label>
          <DatePicker format="MM/DD/YYYY" className="min-w-[150px]" />
        </div>

        <Button type="primary" size="large" className="bg-gray-700 text-white mt-4 sm:mt-6">
          Calculate
        </Button>
      </div>
    </div>
  );
}
