import { motion } from "framer-motion";

const Hero = () => {
  return (
    <motion.section
      initial={{ opacity: 0, y: 30 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
      className="text-center py-24"
    >
      <h1 className="text-5xl font-extrabold text-white">Welcome to Ekarit Visa Service</h1>
      <p className="mt-4 text-xl text-white/80">Where comfort meets luxury.</p>
    </motion.section>
  );
};
export default Hero;
