import React, { useState } from "react";

const Contact: React.FC = () => {
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setSubmitted(true);
  };

  return (
    <section id="contact" className="py-20 bg-blue-50">
      <div className="container mx-auto px-4">
        <h3 className="text-3xl font-semibold text-center mb-6">Contact Us</h3>
        {submitted ? (
          <div className="text-center text-green-600 font-semibold">Thank you! We'll be in touch shortly.</div>
        ) : (
          <form onSubmit={handleSubmit} className="max-w-xl mx-auto space-y-4">
            <input type="text" placeholder="Your Name" required className="w-full p-3 border rounded-md" />
            <input type="email" placeholder="Your Email" required className="w-full p-3 border rounded-md" />
            <textarea placeholder="Your Message" required className="w-full p-3 border rounded-md h-32" />
            <button type="submit" className="bg-blue-600 text-white px-6 py-2 rounded-md font-semibold hover:bg-blue-700">
              Send Message
            </button>
          </form>
        )}
      </div>
    </section>
  );
};

export default Contact;
