import React from "react";

const About: React.FC = () => {
  return (
    <section id="about" className="py-16 bg-gray-100">
      <div className="max-w-5xl mx-auto px-4 text-center">
        <h2 className="text-3xl font-bold mb-6 text-gray-800">About Us</h2>
        <p className="text-lg text-gray-700">
          Ekarit Hotel offers a blend of tradition and modern hospitality. Our rooms are designed to offer a relaxing environment for both business and leisure travelers. With over 10 years of experience in the hospitality industry, we ensure your stay is unforgettable.
        </p>
      </div>
    </section>
  );
};

export default About;
