import React from "react";
//import { Button } from "/components/ui/button";
import { Button,Card} from 'antd';
//import { Card, CardContent } from "./components/ui/card";
import { Phone, Mail, MapPin } from "lucide-react";
import background from '../images/Ekaritservice1.jpg';
import background2 from '../images/Ekaritphoto.jpg';
import { Link } from "react-router-dom";
import { motion } from "framer-motion";

const cardVariants = {
  hidden: { opacity: 0, y: 40 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
};

const Home: React.FC = () => {
  return (
    
<div className="min-h-screen bg-white text-gray-800">
      {/* Header */}
     <header className="bg-[#1c1c1e] text-white p-6 shadow-md">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold">Ekarit Visa Service</h1>
          <nav className="space-x-6">
            {/*}<Link to="/services" className="hover:underline">Services</Link>
            <Link to="/about" className="hover:underline">About</Link>
            <Link to="/contact" className="hover:underline">Contact</Link>*/}
            <a href="#home" className="hover:underline">Home</a>
             <a href="#services" className="hover:underline">Services</a>
            <a href="#about" className="hover:underline">About</a>
            <a href="#contact" className="hover:underline">Contact</a>
          </nav>
        </div>
      </header>
      
     <section className="w-full flex justify-center items-center gap-8 p-8">
      <img
        src={background2}
        alt="Ekarit 1"
        className="w-1/2 h-auto object-contain"
      />
     <div className="flex flex-col gap-6">
      <motion.div
  variants={cardVariants}
  initial="hidden"
  animate="visible"
  whileHover={{ scale: 1.05 }}
>
    <Card
      key={1}
      className="w-[400px] h-auto p-8 bg-[#1c1c1e] rounded-2xl shadow-lg text-white text-center"
    >
      <h1 className="text-4xl font-bold">Welcome to Our Service</h1>
      <p className="mt-4 text-lg">We deliver quality with speed.</p>
    </Card>
</motion.div>
<motion.div
  variants={cardVariants}
  initial="hidden"
  animate="visible"
  whileHover={{ scale: 1.05 }}
>
    <Card
      key={2}
      className="w-[400px] h-auto p-8 bg-[#2b2b2e] rounded-2xl shadow-lg text-white text-center"
    >
      <h1 className="text-3xl font-semibold">Another Highlight</h1>
      <p className="mt-4 text-base">Explore more with our extended services.</p>
    </Card>
</motion.div>
<motion.div
  variants={cardVariants}
  initial="hidden"
  animate="visible"
  whileHover={{ scale: 1.05 }}
>
    <Card
      key={3}
      className="w-[400px] h-auto p-8 bg-[#2b2b2e] rounded-2xl shadow-lg text-white text-center"
    >
      <h1 className="text-3xl font-semibold">Another Highlight</h1>
      <p className="mt-4 text-base">Explore more with our extended services.</p>
    </Card>
    </motion.div>
  </div>
     
    </section>

      {/* Footer 
      <footer className="bg-yellow-400 text-white text-center py-6">
        <p>&copy; {new Date().getFullYear()} ServicePro. All rights reserved.</p>
      </footer>*/}
    </div>
  );
};

export default Home;
