import React from "react";

const Hero: React.FC = () => {
  return (
    <section
      className="h-screen bg-cover bg-center flex items-center justify-center text-white"
      style={{ backgroundImage: "url('/images/hotel-hero.jpg')" }}
    >
      <div className="bg-black bg-opacity-60 p-10 rounded-xl text-center">
        <h1 className="text-4xl md:text-6xl font-bold mb-4">Welcome to Ekarit Hotel</h1>
        <p className="text-xl md:text-2xl mb-6">Comfort. Luxury. Relaxation.</p>
        <a href="#contact" className="px-6 py-3 bg-indigo-600 hover:bg-indigo-700 rounded-lg text-white font-semibold transition">
          Book Now
        </a>
      </div>
    </section>
  );
};

export default Hero;
