import React from "react";

const Hero: React.FC = () => {
  return (
    <section className="bg-blue-600 text-white py-24 text-center">
      <h2 className="text-4xl font-bold mb-4">Professional Solutions for Your Business</h2>
      <p className="text-lg mb-6">Empowering companies with innovative services and expert advice.</p>
      <a href="#contact" className="bg-white text-blue-600 px-6 py-2 rounded-md font-semibold hover:bg-gray-100">
        Get Started
      </a>
    </section>
  );
};
export default Hero;
