import React from "react";
//import { Button } from "/components/ui/button";
import { Button,Card} from 'antd';
//import { Card, CardContent } from "./components/ui/card";
import { Phone, Mail, MapPin } from "lucide-react";
import background from '../images/Ekaritservice1.jpg';
import background2 from '../images/Ekaritphoto.jpg';
import { Link } from "react-router-dom";

const Home: React.FC = () => {
  return (
<div className="min-h-screen bg-white text-gray-800">
      {/* Header */}
     <header className="bg-yellow-400 text-white p-6 shadow-md">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold">Ekarit Visa Service</h1>
          <nav className="space-x-6">
            {/*}<Link to="/services" className="hover:underline">Services</Link>
            <Link to="/about" className="hover:underline">About</Link>
            <Link to="/contact" className="hover:underline">Contact</Link>*/}

             <a href="#services" className="hover:underline">Services</a>
            <a href="#about" className="hover:underline">About</a>
            <a href="#contact" className="hover:underline">Contact</a>
          </nav>
        </div>
      </header>
      
     <section className="w-full flex justify-center items-center gap-4 p-4">
      <img
        src={background2}
        alt="Ekarit 1"
        className="w-1/2 h-auto object-contain"
      />
      <div className="text-center">
    <h1 className="text-4xl font-bold">Welcome to Our Service</h1>
    <p className="mt-4 text-lg">We deliver quality with speed.</p>
  </div>
    </section>

    <section id="service" className="py-16 bg-white">
        <div className="min-h-screen bg-yellow-400 text-white px-4 py-12">
      <div className="text-center mb-10">
        <h1 className="text-4xl font-bold">
          The Services you can get from our company are:
        </h1>
      </div>
    
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 max-w-6xl mx-auto">
        {services.map((service, idx) => (
          <Card
            key={idx}
            className="bg-[#1c1c1e] rounded-2xl shadow-lg text-white text-center hover:shadow-xl transition-shadow duration-300"
            bodyStyle={{ padding: "30px" }}
          >
            <div className="flex justify-center mb-4">{service.icon}</div>
            <h3 className="text-xl font-bold mb-3">{service.title}</h3>
            <p className="text-sm text-gray-300">{service.description}</p>
          </Card>
        ))}
      </div>
    </div>
    </section>

      {/* Footer 
      <footer className="bg-yellow-400 text-white text-center py-6">
        <p>&copy; {new Date().getFullYear()} ServicePro. All rights reserved.</p>
      </footer>*/}
    </div>
  );
};

export default Home;
