import React from "react";

const Hero: React.FC = () => {
  return (
    <section
      className="h-screen bg-cover bg-center flex items-center justify-center"
      style={{ backgroundImage: "url('/images/hero-bg.jpg')" }}
    >
      <div className="backdrop-blur-md bg-white/10 rounded-xl p-10 text-center max-w-xl shadow-2xl border border-white/30">
        <h1 className="text-5xl font-extrabold mb-4 drop-shadow-md">
          Discover Luxury
        </h1>
        <p className="text-lg mb-6 text-white/90">
          Ekarit Hotel - Where Elegance Meets Comfort
        </p>
        <a
          href="#contact"
          className="inline-block bg-indigo-600 hover:bg-indigo-700 transition px-6 py-3 rounded-lg font-semibold shadow-md"
        >
          Book Your Stay
        </a>
      </div>
    </section>
  );
};

export default Hero;
